    /**
     * Node constructor.
     * @param $next
     * @param $data
     * @param $prev
     */
    public function __construct($data, $next,$prev)
    {
        $this->setData($data);
        $this->setNext($next);
        $this->setPrev($prev);
    }

    /**
     *  Functional description :
