        if ($this->getHeader() != null){
            return ;
        }

        //
        $currentNode = $this->getHeader()->getNext();
        //
        $next=null;
        $prev=null;//,null
        //rootNode->0->1->2->3 -->  3->2->1->0
        //
        while ($currentNode != null){
            //
            $next = $currentNode->getNext();
            //
            $currentNode->setNext($prev);
            $currentNode->setPrev($next);

            #
            $prev = $currentNode;
            $currentNode = $next;

        }
        return $prev;
    }

}
