<?php 
abstract class Builder
{
  protected $car;
  abstract public function buildPartA();
  abstract public function buildPartB();
  abstract public function buildPartC();
  abstract public function getResult();
}
class CarBuilder extends Builder
{
  function __construct()
  {
      $this->car = new Car();
  }
  public function buildPartA(){
      $this->car->setPartA('');
  }
  public function buildPartB(){
      $this->car->setPartB('');
  }
  public function buildPartC(){
      $this->car->setPartC('');
  }
  public function getResult(){
      return $this->car;
  }
}
class Car
{
  protected $partA;
  protected $partB;
  protected $partC;
  public function setPartA($str){
      $this->partA = $str;
  }
  public function setPartB($str){
      $this->partB = $str;
  }
  public function setPartC($str){
      $this->partC = $str;
  }
  public function show()
  {
      echo "".$this->partA.','.$this->partB.','.$this->partC.'';
  }
}
class Director
{
  public $myBuilder;

  public function startBuild()
  {
      $this->myBuilder->buildPartA();
      $this->myBuilder->buildPartB();
      $this->myBuilder->buildPartC();
      return $this->myBuilder->getResult();
  }

  public function setBuilder(Builder $builder)
  {
      $this->myBuilder = $builder;
  }
}
$carBuilder = new CarBuilder();
$director = new Director();
$director->setBuilder($carBuilder);
$newCar = $director->startBuild();
$newCar->show();
```


