function binarySearch($data,$min,$max,$k){
    if ($min <= $max){
        //
        $mid = intval(($min +$max)/2);
        if ($data[$mid] == $k){
            //,
            return $mid;
        } else if ($k < $data[$mid]){
            //
            return binarySearch($data, $min, $mid-1, $k);
        } else {
            //
            return binarySearch($data, $mid+1, $max, $k);
        }
    }
    return false;
}

